import string
from collections import Counter 
a = ""
ALPHABET = string.ascii_uppercase

array = [[(i+j)%26 for j in range(26)] for i in range(26)]
key = "GNWJVWN"
origin = ""
test = ""
for i in range(len(a)):
    row = ALPHABET.index(key[i%7])    
    c = ALPHABET.index(a[i])
    column = array[row].index(c)
    origin += ALPHABET[column]
    if i % 7 == 2:
        test += ALPHABET[column]
res = Counter(test)
print(origin)
print(res)


